	var desktopMin =  901;
	var mobileMaxWidth = desktopMin-1;
	var mobileTransitionSpeed = 300;
	var togglechild = '<div class="togglechild"><a href="#" class="toggle"><span class="fa fa-plus-square"></span><span class="sr-only">expand nav</span></a></div>';
	
	// handling resizing of document and navigation toggle for handhelds
	$('nav.top').prepend('<a href="#" id="navtoggle"><span class="fa fa-navicon"></span> menu<span class="sr-only">use arrow keys to navigate the menu</span></a>');
	

